import { ContentTypeId, ContentCodec, EncodedContent } from '@xmtp/content-type-primitives';

declare const ContentTypeText: ContentTypeId;
declare enum Encoding {
    utf8 = "UTF-8",
    unknown = "unknown"
}
type TextParameters = {
    encoding: Encoding;
};
declare class TextCodec implements ContentCodec<string, TextParameters> {
    get contentType(): ContentTypeId;
    encode(content: string): {
        type: ContentTypeId;
        parameters: {
            encoding: Encoding;
        };
        content: Uint8Array;
    };
    decode(content: EncodedContent<TextParameters>): string;
    fallback(): undefined;
    shouldPush(): boolean;
}

export { ContentTypeText, Encoding, TextCodec, type TextParameters };
